<?phpnamespace KnysakPatryk\MediaQuerySuppressor;use KnysakPatryk\MediaQuerySuppressor\Strategy\SuppressionStrategyInterface;class Suppressor{    private $suppressionStrategy;    public function __construct(SuppressionStrategyInterface $suppressionStrategy)    {        $this->suppressionStrategy = $suppressionStrategy;    }    public function one($input)    {        return $this->suppressionStrategy->suppress($input);    }    public function many($inputs)    {        foreach ($inputs as $key => $input) {            $inputs[$key] = $this->one($input);        }        return $inputs;    }}